    private void hapusActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_hapusActionPerformed
        tahun.setText("");
        bulan.setSelectedIndex(0);
        hasil.setText("");
        tahun.requestFocus();
    }//GEN-LAST:event_hapusActionPerformed

    private void keluarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_keluarActionPerformed
        int konfirmasi = javax.swing.JOptionPane.showConfirmDialog(this, "Yakin mau keluar?", "Konfirmasi", javax.swing.JOptionPane.YES_NO_OPTION);
        if (konfirmasi == javax.swing.JOptionPane.YES_OPTION) {
            System.exit(0);
        }
    }//GEN-LAST:event_keluarActionPerformed

    private void simpanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_simpanActionPerformed
        try {
            if (hasil.getText().isEmpty()) {
                javax.swing.JOptionPane.showMessageDialog(this, "Belum ada hasil yang akan disimpan!");
                return;
            }
            java.io.BufferedWriter bw = new java.io.BufferedWriter(new java.io.FileWriter("hasil_penentu_jumlah_hari.txt", true));
            bw.write(hasil.getText());
            bw.newLine();
            bw.close();
            javax.swing.JOptionPane.showMessageDialog(this, "Data berhasil disimpan ke file!");
        } catch (Exception e) {
            javax.swing.JOptionPane.showMessageDialog(this, "Gagal menyimpan file: " + e.getMessage());
        }
    }//GEN-LAST:event_simpanActionPerformed

    private void hitungActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_hitungActionPerformed
        try {
            int tahunInput = Integer.parseInt(tahun.getText());
            int bulanIndex = bulan.getSelectedIndex() + 1; // 1â€“12
            String namaBulan = bulan.getSelectedItem().toString();

            HitungHari hh = new HitungHari();
            int jumlahHari = hh.hitung(tahunInput, bulanIndex);

            hasil.setText("Tahun " + tahunInput + " bulan " + namaBulan + " memiliki " + jumlahHari + " hari.");
        } catch (NumberFormatException e) {
            javax.swing.JOptionPane.showMessageDialog(this, "Masukkan tahun berupa angka!", "Input Error", javax.swing.JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_hitungActionPerformed

